{
  "market_if_touched_order": {
    "name": "Market-if-Touched (MIT) Order",
    "description": "A Market-if-Touched (MIT) order is a conditional order to buy or sell an asset that becomes a market order once the asset's price reaches a specified trigger price. It is used to enter a position or take profit at a price that is more favorable than the current market price.",
    "characteristics": [
      {
        "type": "Buy MIT Order",
        "condition": "The trigger price is set *below* the current market price.",
        "execution": "It is triggered when the market price falls to or touches the specified price. Once triggered, it becomes a market order to buy at the best available price.",
        "use_case": "To enter a long position at a lower price if the market pulls back, or to take profit on an existing short position."
      },
      {
        "type": "Sell MIT Order",
        "condition": "The trigger price is set *above* the current market price.",
        "execution": "It is triggered when the market price rises to or touches the specified price. Once triggered, it becomes a market order to sell at the best available price.",
        "use_case": "To enter a short position at a higher price if the market rallies, or to take profit on an existing long position."
      }
    ]
  },
  "stop_order": {
    "name": "Stop Order",
    "description": "A stop order is a conditional order to buy or sell an asset that becomes a market order once the asset's price reaches a specified trigger price (the 'stop price'). It is typically used to limit losses on an existing position (a 'stop-loss') or to enter a new position when the price breaks through a key level.",
    "characteristics": [
      {
        "type": "Buy Stop Order",
        "condition": "The stop price is set *above* the current market price.",
        "execution": "It is triggered when the market price rises to or surpasses the stop price. Once triggered, it becomes a market order to buy at the best available price.",
        "use_case": "To limit the loss on a short position or to enter a long position on a breakout above a resistance level, betting on continued upward momentum."
      },
      {
        "type": "Sell Stop Order",
        "condition": "The stop price is set *below* the current market price.",
        "execution": "It is triggered when the market price falls to or drops below the stop price. Once triggered, it becomes a market order to sell at the best available price.",
        "use_case": "To limit the loss on a long position (the most common stop-loss) or to enter a short position on a breakdown below a support level, betting on continued downward momentum."
      }
    ]
  },
  "summary_of_differences": {
    "mit_order": "Placed to execute at a price *more favorable* than the current market (buy below, sell above). Used for entering on pullbacks/rallies or taking profit.",
    "stop_order": "Placed to execute at a price *less favorable* than the current market (buy above, sell below). Used to limit losses or enter on breakouts."
  }
}
